package com.Polodz.service;import info.talacha.filmweb.api.FilmwebApi;import info.talacha.filmweb.connection.FilmwebException;import info.talacha.filmweb.models.Film;import info.talacha.filmweb.search.models.FilmSearchResult;import java.util.ArrayList;import java.util.List;import java.util.Optional;/** * Created by Łukasz on 2016-09-06. */public class FilmWebApiServiceImpl implements FilmWebApiService {    public String getInfo(String tytul) throws FilmwebException {        FilmwebApi fa = new FilmwebApi();        String strings = new String();        List<FilmSearchResult> filmInfoList = fa.findFilm(tytul);        if(!filmInfoList.isEmpty()) {            Film f = fa.getFilmData(filmInfoList.get(0).getId());            return "Tytuł: " + f.getTitle() + " PolishTitle: " + f.getPolishTitle() + " Countries: " + f.getCountries() + " LICZBA GLOSÓW " + f.getVotes().toString() + " SREDNIA OCEN: " + f.getRate().toString();        }        return "Brak Danych";    }    public String getRatingofFilm(String tytuł) throws FilmwebException {        FilmwebApi fa = new FilmwebApi();        List<String> strings = new ArrayList<>();        List<FilmSearchResult> filmInfoList = fa.findFilm(tytuł);        if(!filmInfoList.isEmpty()) {            Film f = fa.getFilmData(filmInfoList.get(0).getId());            return f.getRate().toString();        }        return "Brak Danych";    }    public String getInterestedOfFilm(String tytuł) throws FilmwebException {        FilmwebApi fa = new FilmwebApi();        List<String> strings = new ArrayList<>();        List<FilmSearchResult> filmInfoList = fa.findFilm(tytuł);        if(!filmInfoList.isEmpty()) {            Film f = fa.getFilmData(filmInfoList.get(0).getId());            return f.getVotes().toString();        }        return "Brak Danych";    }}